import React, { useContext, useState } from "react";
import { AntDesign } from "@expo/vector-icons";
import { View, StyleSheet, TouchableNativeFeedback } from "react-native";
import { Avatar, Title, Caption, Drawer, Text } from "react-native-paper";
import { DrawerContentScrollView, DrawerItem } from "@react-navigation/drawer";
import { globalstyles } from "../styles/global";
import { AuthContext } from "../routes/AuthProvider";

export function DrawerContent(props) {
  const { user, logout, userData } = useContext(AuthContext);

  return (
    <View style={globalstyles.container}>
      <DrawerContentScrollView {...props}>
        <View style={styles.drawerContent}>
          <View style={{ alignItems: "flex-end", marginRight: 15 }}>
            <AntDesign
              name="close"
              size={25}
              color="white"
              onPress={() => props.navigation.closeDrawer()}
            />
          </View>

          {!user ? null : (
            <View style={styles.userInfoSection}>
              <View style={styles.profileWrapper}>
                <Avatar.Image
                  source={{
                    uri: "https://avatarfiles.alphacoders.com/153/153690.png",
                  }}
                  size={50}
                />
                <View style={{ marginLeft: 20 }}>
                  {userData == undefined || userData.username == null ? (
                    <Title style={styles.title}>User_Name</Title>
                  ) : (
                    <Title style={styles.title}>{userData.username}</Title>
                  )}

                  <Caption style={styles.caption}>@45751</Caption>
                  <TouchableNativeFeedback
                    onPress={() => props.navigation.navigate("Profile")}
                  >
                    <View style={{ marginTop: 2 }}>
                      <Text style={styles.Edit}>Edit</Text>
                    </View>
                  </TouchableNativeFeedback>
                </View>
              </View>
            </View>
          )}
        </View>
        {!user ? (
          <Drawer.Section
            style={{
              marginTop: 80,
              justifyContent: "center",
              alignContent: "center",
              borderColor: "#272727",
              borderWidth: 1,
              height: 58,
            }}
          >
            <DrawerItem
              label="HOME"
              labelStyle={{
                color: "#EDEDED",
                fontSize: 14,
                alignSelf: "center",
                fontFamily: "Poppins-Medium",
              }}
              onPress={() => {
                props.navigation.navigate("Home");
              }}
            />
          </Drawer.Section>
        ) : (
          <Drawer.Section style={styles.drawerSection}>
            <DrawerItem
              label="HOME"
              labelStyle={{
                color: "#EDEDED",
                fontSize: 14,
                alignSelf: "center",
                fontFamily: "Poppins-Medium",
              }}
              onPress={() => {
                props.navigation.navigate("Home");
              }}
            />
          </Drawer.Section>
        )}
        <Drawer.Section style={styles.drawerSection}>
          <DrawerItem
            label="Pf"
            labelStyle={{
              color: "#EDEDED",
              fontSize: 14,
              alignSelf: "center",
              paddingLeft: 6,
              fontFamily: "Poppins-Medium",
            }}
            onPress={() => {
              props.navigation.navigate("ProfileComplete");
            }}
          />
        </Drawer.Section>
        <Drawer.Section style={styles.drawerSection}>
          <DrawerItem
            label="COUPONS"
            labelStyle={{
              color: "#EDEDED",
              fontSize: 14,
              alignSelf: "center",
              paddingLeft: 6,
              fontFamily: "Poppins-Medium",
            }}
            onPress={() => {
              props.navigation.navigate("Coupon");
            }}
          />
        </Drawer.Section>
        {!user ? null : (
          <Drawer.Section style={styles.drawerSection}>
            <DrawerItem
              label="SCANNER"
              labelStyle={{
                color: "#EDEDED",
                fontSize: 14,
                alignSelf: "center",
                paddingLeft: 6,
                fontFamily: "Poppins-Medium",
              }}
              onPress={() => {
                props.navigation.navigate("Barcode");
              }}
            />
          </Drawer.Section>
        )}
        {!user ? null : (
          <Drawer.Section style={styles.drawerSection}>
            <DrawerItem
              label="TRANSACTIONS"
              labelStyle={{
                color: "#EDEDED",
                fontSize: 14,
                alignSelf: "center",
                paddingLeft: 6,
                fontFamily: "Poppins-Medium",
              }}
              onPress={() => {
                props.navigation.navigate("Transaction");
              }}
            />
          </Drawer.Section>
        )}
        <Drawer.Section style={styles.drawerSection}>
          <DrawerItem
            label="HELP"
            labelStyle={{
              color: "#EDEDED",
              fontSize: 14,
              alignSelf: "center",
              paddingLeft: 6,
              fontFamily: "Poppins-Medium",
            }}
            onPress={() => {
              props.navigation.navigate("Help");
            }}
          />
        </Drawer.Section>
        <Drawer.Section style={styles.drawerSection}>
          <DrawerItem
            label="Get Coupon"
            labelStyle={{
              color: "#EDEDED",
              fontSize: 14,
              alignSelf: "center",
              paddingLeft: 6,
              fontFamily: "Poppins-Medium",
            }}
            onPress={() => {
              props.navigation.navigate("GetCoupon");
            }}
          />
        </Drawer.Section>
        <Drawer.Section style={styles.drawerSection}>
          <DrawerItem
            label="ABOUT US"
            labelStyle={{
              color: "#EDEDED",
              fontSize: 14,
              alignSelf: "center",
              paddingLeft: 6,
              fontFamily: "Poppins-Medium",
            }}
            onPress={() => {
              props.navigation.navigate("About");
            }}
          />
        </Drawer.Section>
        {!user ? null : (
          <Drawer.Section style={styles.drawerSection}>
            <DrawerItem
              label="LOG OUT"
              labelStyle={{
                color: "#EDEDED",
                fontSize: 14,
                alignSelf: "center",
                paddingLeft: 6,
                fontFamily: "Poppins-Medium",
              }}
              onPress={() =>
                logout()
                  ? props.navigation.closeDrawer()
                  : console.log("not closed")
              }
            />
          </Drawer.Section>
        )}
      </DrawerContentScrollView>
      <Drawer.Section style={styles.bottomDrawerWrapper}>
        <Text style={styles.bottomDrawer}>version v0.1</Text>
      </Drawer.Section>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    borderColor: "#525252",
    backgroundColor: "white",
  },

  drawerContent: {
    flex: 1,
  },
  userInfoSection: {
    paddingLeft: 25,
  },
  title: {
    fontSize: 16,
    marginTop: 3,
    color: "#EDEDED",
  },
  caption: {
    fontSize: 14,
    lineHeight: 14,
    color: "#EDEDED",
  },
  drawerSection: {
    marginTop: -6,
    justifyContent: "center",
    alignContent: "center",
    borderColor: "#272727",
    borderWidth: 1,
    height: 58,
  },
  profileWrapper: {
    marginTop: 50,
    marginBottom: 21,
    flexDirection: "row",
  },
  bottomDrawerWrapper: {
    alignItems: "center",
    marginBottom: 20,
  },
  bottomDrawer: {
    fontSize: 12,
    color: "#808080",
  },
  Edit: {
    color: "#EDEDED",
    backgroundColor: "#000000",
    fontSize: 14,
    marginTop: 4,
    width: 60,
    textAlign: "center",
    fontFamily: "Poppins-Medium",
    borderWidth: 1,
    borderColor: "#525252",
    borderRadius: 3,
    paddingTop: 4,
  },
});
