import "react-native-gesture-handler";
import React, { useState } from "react";
import { Picker } from "@react-native-picker/picker";
import {
  Button,
  View,
  Text,
  StyleSheet,
  TouchableNativeFeedback,
  Image,
  StatusBar,
  TextInput,
} from "react-native";
import { AuthContext } from "../routes/AuthProvider";
import { globalstyles } from "../styles/global";
import { db } from "../firebases";

export default function ProfileComplete({ navigation }) {
  const [location, setLocation] = useState("bhilai");
  const [fullName, setFullName] = useState();
  const [age, setAge] = useState();
  const [gender, setGender] = useState("male");
  const { user } = React.useContext(AuthContext);
  const [locationOptions, setLocationOptions] = useState([
    { value: "bhilai", label: "Bhilai", key: "Bhilai" },
    { value: "raipur", label: "Raipur", key: "Raipur" },
  ]);
  const [genderOptions, setgenderOptions] = useState([
    { value: "male", label: "male", key: "male" },
    { value: "female", label: "female", key: "female" },
    { value: "other", label: "other", key: "other" },
  ]);
  function finish() {
    const data = {
      Coupons: {},
      Username: fullName,
      history: {},
      id: user.uid,
      location: location,
      age: age,
      gender: gender,
      phone: user.phoneNumber,
    };
    const userDoc = db.collection("Users").doc(user.uid);
    userDoc.set(data).catch((err) => {
      console.log(err);
    });
    navigation.navigate("Home");
  }
  return (
    <View style={globalstyles.container}>
      <StatusBar backgroundColor="black" barStyle="light-content" />
      <View style={styles.banner}>
        <Image></Image>
      </View>
      <View style={styles.welcomeBackWrapper}>
        <Text style={styles.welcomeBack}>Complete profile</Text>
      </View>
      <View>
        <TextInput
          style={styles.textinput}
          placeholder="Full Name"
          placeholderTextColor="#EDEDED"
          color="#fff"
          onChangeText={(text) => setFullName(text)}
        ></TextInput>
      </View>
      <View>
        <TextInput
          style={[styles.textinput, styles.textPassword]}
          placeholder="Age"
          placeholderTextColor="#EDEDED"
          color="#fff"
          onChangeText={(age) => setAge(age)}
        ></TextInput>
      </View>
      <View style={styles.picker}>
        <Picker
          style={{ width: "100%", height: 61, color: "#fff" }}
          selectedValue={location}
          onValueChange={(itemValue, itemIndex) => setLocation(itemValue)}
        >
          {locationOptions.map((item) => {
            return (
              <Picker.Item
                label={item.label}
                value={item.value}
                key={item.key}
              />
            );
          })}
        </Picker>
      </View>
      <View style={styles.picker}>
        <Picker
          style={{ width: "100%", height: 61, color: "#fff" }}
          selectedValue={gender}
          onValueChange={(itemValue, itemIndex) => setGender(itemValue)}
        >
          {genderOptions.map((item) => {
            return (
              <Picker.Item
                label={item.label}
                value={item.value}
                key={item.key}
              />
            );
          })}
        </Picker>
      </View>
      <View style={styles.ProfileCompleteWrapper}>
        <TouchableNativeFeedback useForeground={true} onPress={finish}>
          <View style={styles.ProfileCompleteButton}>
            <Text style={styles.ProfileCompleteButtonTitle}>Finish</Text>
          </View>
        </TouchableNativeFeedback>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  picker: {
    backgroundColor: "#1A1A1A",
    borderColor: "#424242",
    color: "#ffffff",
    borderWidth: 1,
    borderRadius: 20,
    paddingLeft: 20,
    paddingRight: 20,
    width: "80%",
    height: 61,
    alignSelf: "center",
    fontSize: 18,
    fontFamily: "Poppins-Regular",
    marginBottom: 24,
  },
  ProfileCompleteWrapper: {
    marginTop: 30,
  },
  ProfileCompleteButton: {
    justifyContent: "center",
    alignItems: "center",
    alignSelf: "center",
    width: 178,
    borderRadius: 20,
    height: 58,
    borderColor: "#fff",
    borderStyle: "solid",
    borderWidth: 1,
  },
  ProfileCompleteButtonTitle: {
    color: "#ffffff",
    fontSize: 18,
    fontFamily: "Poppins-Medium",
  },
  banner: {
    height: "16%",
    backgroundColor: "#70007a",
    width: "100%",
    marginBottom: "2%",
  },
  welcomeBackWrapper: {
    marginBottom: 30,
    marginTop: 20,
  },
  welcomeBack: {
    fontSize: 28,
    fontFamily: "Poppins-Medium",
    color: "#EDEDED",
    alignSelf: "center",
    width: "80%",
  },
  textinput: {
    backgroundColor: "#1A1A1A",
    borderColor: "#424242",
    borderWidth: 1,
    borderRadius: 20,
    paddingLeft: 20,
    paddingRight: 20,
    width: "80%",
    height: 61,
    alignSelf: "center",
    fontSize: 18,
    fontFamily: "Poppins-Regular",
    marginBottom: 24,
  },
  textPassword: {
    marginBottom: 16,
  },
  forgetPass: {
    color: "#fff",
    alignSelf: "center",
    width: "80%",
    margin: 0,
  },
});
