import "react-native-gesture-handler";
import React, { useState, useEffect, useContext } from "react";
import { Picker } from "@react-native-picker/picker";
import {
  Button,
  View,
  Text,
  StyleSheet,
  TouchableNativeFeedback,
  Image,
  StatusBar,
  BackHandler,
  ScrollView,
  TextInput,
} from "react-native";
import { AuthContext } from "../routes/AuthProvider";
import { globalstyles } from "../styles/global";
import { db } from "../firebases";
import { addPushTokenListener } from "expo-notifications";
import Constants from "expo-constants";
import * as Notifications from "expo-notifications";

Notifications.setNotificationHandler({
  handleNotification: async () => ({
    shouldShowAlert: true,
    shouldPlaySound: false,
    shouldSetBadge: false,
  }),
});

export default function ProfileComplete({ navigation }) {
  const [location, setLocation] = useState("bhilai");
  const [fullName, setFullName] = useState();
  const [age, setAge] = useState();
  const [couponArray, setCouponArray] = useState(null);
  const [token, setToken] = useState();
  const [gender, setGender] = useState("male");
  const { user } = useContext(AuthContext);
  const [expoPushToken, setExpoPushToken] = useState("");
  const [referralId, setReferralId] = useState("");
  const [clientName, setClientName] = useState(null);
  const [locationOptions, setLocationOptions] = useState([
    { value: "bhilai", label: "Bhilai", key: "Bhilai" },
    { value: "raipur", label: "Raipur", key: "Raipur" },
  ]);
  const [genderOptions, setgenderOptions] = useState([
    { value: "male", label: "male", key: "male" },
    { value: "female", label: "female", key: "female" },
    { value: "other", label: "other", key: "other" },
  ]);
  BackHandler.addEventListener("hardwareBackPress", function () {
    () => null;
    return true;
  });

  useEffect(() => {
    const tempClientName = [];
    db.collection("ClientData")
      .get()
      .then((res) => {
        res.forEach((doc) => {
          tempClientName.push(doc.data().id);
        });
        setClientName(tempClientName);
      });
  }, []);

  //TAKING THE DETAILS FROM THE USER
  useEffect(() => {
    if (token !== undefined) {
      const data = {
        Coupons: {},
        username: fullName,
        history: {},
        id: user.uid,
        expoToken: token,
        location: location,
        age: age,
        gender: gender,
        phone: user.phoneNumber,
      };
      checkingReferralId();
      setUserData();
      const tempCouponArray = [];
      async function giveCoupons() {
        const randomNumber = Math.floor(Math.random() * clientName.length);
        db.collection("ClientData")
          .doc(clientName[randomNumber])
          .collection("Coupons")
          .where("isAlloted", "==", false)
          .limit(2)
          .get()
          .then(function (querySnapshot) {
            querySnapshot.forEach(function (doc) {
              // doc.data() is never undefined for query doc snapshots
              tempCouponArray.push(doc.data());
            });
          })
          .then(() => {
            //console.log(tempCouponArray);
            tempCouponArray.map((coupon, index) => {
              //console.log(coupon.id);

              var current = new Date();
              current.setHours(current.getHours());
              const x = current.getHours() + "-" + current.getMinutes();
              const d =
                current.getDate() +
                "-" +
                (current.getMonth() + 1) +
                "-" +
                current.getFullYear();
              const datas = {
                activeFromTime: x,
                activeFromDate: d,
                isAlloted: true,
                allotedTo: user.uid,
              };

              if (index === 1) {
                datas.allotedTo = referralId;
              }
              //console.log(datas);
              db.collection("ClientData")
                .doc(coupon.client)
                .collection("Coupons")
                .doc(coupon.id)
                .set(datas, { merge: true })
                .then(() => console.log("done"))
                .catch((err) => {
                  console.log(err);
                });
            });
          });
      }
      async function checkingReferralId() {
        db.collection("Users")
          .doc(referralId == "" ? "No Referral Id" : referralId)
          .get()
          .then((doc) => {
            if (doc.exists) {
              giveCoupons();
            } else {
              if (referralId == "") {
                console.log("User Not Enter Referral Id");
              } else {
                alert("Not Valid Id");
              }
            }
          });
      }
      async function setUserData() {
        const userDoc = db.collection("Users").doc(user.uid);
        userDoc
          .set(data)
          .then(() => navigation.navigate("AppIntroSliders"))
          .catch((err) => {
            console.log(err);
          });
      }
    } else {
    }
  }, [token]);

  //CHECKING THE USER MOBILE ( USING EMULATOR OR NOT ) IF NOT TAKING THE TOKEN FROM THE USER
  checkingTheUserMobile = async () => {
    registerForPushNotificationsAsync().then((token) =>
      setExpoPushToken(token)
    );
    async function registerForPushNotificationsAsync() {
      let token;
      if (Constants.isDevice) {
        const {
          status: existingStatus,
        } = await Notifications.getPermissionsAsync();
        let finalStatus = existingStatus;
        if (existingStatus !== "granted") {
          const { status } = await Notifications.requestPermissionsAsync();
          finalStatus = status;
        }
        if (finalStatus !== "granted") {
          alert("Failed to get push token for push notification!");
          return;
        }
        tokens = (await Notifications.getExpoPushTokenAsync()).data;
        //console.log(tokens);
        setToken(tokens);
      } else {
        alert("Must use physical device for Push Notifications");
      }

      if (Platform.OS === "android") {
        Notifications.setNotificationChannelAsync("default", {
          name: "default",
          importance: Notifications.AndroidImportance.MAX,
          vibrationPattern: [0, 250, 250, 250],
          lightColor: "#FF231F7C",
        });
      }

      return token;
    }
  };

  return (
    <View style={globalstyles.container}>
      <StatusBar backgroundColor="black" barStyle="light-content" />
      <View style={styles.banner}>
        <Image></Image>
      </View>
      <View style={styles.welcomeBackWrapper}>
        <Text style={styles.welcomeBack}>Complete profile</Text>
      </View>
      <ScrollView>
        <View>
          <TextInput
            style={styles.textinput}
            placeholder="Full Name"
            placeholderTextColor="#EDEDED"
            color="#fff"
            onChangeText={(text) => setFullName(text)}
          ></TextInput>
        </View>
        <View>
          <TextInput
            style={[styles.textinput, styles.textPassword]}
            placeholder="Age"
            placeholderTextColor="#EDEDED"
            color="#fff"
            onChangeText={(age) => setAge(age)}
          ></TextInput>
        </View>
        <View style={styles.picker}>
          <Picker
            style={{ width: "100%", height: 61, color: "#fff" }}
            selectedValue={location}
            onValueChange={(itemValue, itemIndex) => setLocation(itemValue)}
          >
            {locationOptions.map((item) => {
              return (
                <Picker.Item
                  label={item.label}
                  value={item.value}
                  key={item.key}
                />
              );
            })}
          </Picker>
        </View>
        <View style={styles.picker}>
          <Picker
            style={{ width: "100%", height: 61, color: "#fff" }}
            selectedValue={gender}
            onValueChange={(itemValue, itemIndex) => setGender(itemValue)}
          >
            {genderOptions.map((item) => {
              return (
                <Picker.Item
                  label={item.label}
                  value={item.value}
                  key={item.key}
                />
              );
            })}
          </Picker>
        </View>
        <View>
          <TextInput
            style={styles.textinput}
            placeholder="Referral Id(Optional)"
            placeholderTextColor="#EDEDED"
            color="#fff"
            onChangeText={(referralId) => setReferralId(referralId)}
          ></TextInput>
        </View>
        <View style={styles.ProfileCompleteWrapper}>
          <TouchableNativeFeedback
            useForeground={true}
            onPress={checkingTheUserMobile}
          >
            <View style={styles.ProfileCompleteButton}>
              <Text style={styles.ProfileCompleteButtonTitle}>Finish</Text>
            </View>
          </TouchableNativeFeedback>
        </View>
      </ScrollView>
    </View>
  );
}

